import{_ as r,c as n,o as e,ae as l}from"./chunks/framework.BBQ6_s69.js";const u=JSON.parse('{"title":"后端组件依赖","description":"","frontmatter":{},"headers":[],"relativePath":"core-concepts/backend-dependencies.md","filePath":"core-concepts/backend-dependencies.md","lastUpdated":1765869554000}'),o={name:"core-concepts/backend-dependencies.md"};function t(i,a,s,g,h,c){return e(),n("div",null,a[0]||(a[0]=[l('<h1 id="后端组件依赖" tabindex="-1">后端组件依赖 <a class="header-anchor" href="#后端组件依赖" aria-label="Permalink to &quot;后端组件依赖&quot;">​</a></h1><h2 id="技术栈概览" tabindex="-1">技术栈概览 <a class="header-anchor" href="#技术栈概览" aria-label="Permalink to &quot;技术栈概览&quot;">​</a></h2><p>WHartTest 后端基于 Django REST Framework 构建，采用 Python 生态的现代化技术栈，提供高性能、可扩展的 RESTful API 服务。</p><h2 id="版本控制说明" tabindex="-1">版本控制说明 <a class="header-anchor" href="#版本控制说明" aria-label="Permalink to &quot;版本控制说明&quot;">​</a></h2><p>在依赖管理中，我们使用以下版本控制符号：</p><ul><li><strong>==</strong>: 精确版本匹配（如 <code>Django==5.2</code>）</li><li><strong>&gt;=</strong>: 大于或等于指定版本</li><li><strong>^</strong>: 兼容版本（主版本号不变，次版本号和修订号可以升级）</li><li><strong>~</strong>: 近似版本（次版本号不变，修订号可以升级）</li></ul><h2 id="核心框架" tabindex="-1">核心框架 <a class="header-anchor" href="#核心框架" aria-label="Permalink to &quot;核心框架&quot;">​</a></h2><h3 id="django" tabindex="-1">Django <a class="header-anchor" href="#django" aria-label="Permalink to &quot;Django&quot;">​</a></h3><ul><li><strong>版本</strong>: 5.2</li><li><strong>作用</strong>: 高级 Python Web 框架，遵循 MTV 架构模式</li><li><strong>特点</strong>: <ul><li>内置 ORM、认证系统、管理后台</li><li>强大的中间件支持</li><li>完善的文档和社区</li></ul></li><li><strong>开源协议</strong>: BSD License</li></ul><h3 id="django-rest-framework" tabindex="-1">Django REST Framework <a class="header-anchor" href="#django-rest-framework" aria-label="Permalink to &quot;Django REST Framework&quot;">​</a></h3><ul><li><strong>版本</strong>: 3.16.0</li><li><strong>作用</strong>: Django 的 RESTful API 扩展框架</li><li><strong>特点</strong>: <ul><li>序列化/反序列化</li><li>认证和权限控制</li><li>视图集和路由</li><li>内置 API 文档</li></ul></li><li><strong>开源协议</strong>: BSD License</li></ul><h2 id="身份认证" tabindex="-1">身份认证 <a class="header-anchor" href="#身份认证" aria-label="Permalink to &quot;身份认证&quot;">​</a></h2><h3 id="django-rest-framework-simple-jwt" tabindex="-1">Django REST Framework Simple JWT <a class="header-anchor" href="#django-rest-framework-simple-jwt" aria-label="Permalink to &quot;Django REST Framework Simple JWT&quot;">​</a></h3><ul><li><strong>版本</strong>: 5.3.1</li><li><strong>作用</strong>: JWT 认证支持</li><li><strong>开源协议</strong>: MIT License</li></ul><h2 id="api-文档" tabindex="-1">API 文档 <a class="header-anchor" href="#api-文档" aria-label="Permalink to &quot;API 文档&quot;">​</a></h2><h3 id="drf-spectacular" tabindex="-1">DRF Spectacular <a class="header-anchor" href="#drf-spectacular" aria-label="Permalink to &quot;DRF Spectacular&quot;">​</a></h3><ul><li><strong>版本</strong>: 0.28.0</li><li><strong>作用</strong>: API Schema 生成和文档</li><li><strong>开源协议</strong>: BSD License</li></ul><h2 id="跨域支持" tabindex="-1">跨域支持 <a class="header-anchor" href="#跨域支持" aria-label="Permalink to &quot;跨域支持&quot;">​</a></h2><h3 id="django-cors-headers" tabindex="-1">Django CORS Headers <a class="header-anchor" href="#django-cors-headers" aria-label="Permalink to &quot;Django CORS Headers&quot;">​</a></h3><ul><li><strong>版本</strong>: 4.3.1</li><li><strong>作用</strong>: CORS 跨域支持</li><li><strong>开源协议</strong>: MIT License</li></ul><h2 id="环境配置" tabindex="-1">环境配置 <a class="header-anchor" href="#环境配置" aria-label="Permalink to &quot;环境配置&quot;">​</a></h2><h3 id="python-dotenv" tabindex="-1">Python Dotenv <a class="header-anchor" href="#python-dotenv" aria-label="Permalink to &quot;Python Dotenv&quot;">​</a></h3><ul><li><strong>版本</strong>: 1.1.1</li><li><strong>作用</strong>: 环境变量加载</li><li><strong>开源协议</strong>: BSD License</li></ul><h2 id="mcp-工具支持" tabindex="-1">MCP 工具支持 <a class="header-anchor" href="#mcp-工具支持" aria-label="Permalink to &quot;MCP 工具支持&quot;">​</a></h2><h3 id="fastmcp" tabindex="-1">FastMCP <a class="header-anchor" href="#fastmcp" aria-label="Permalink to &quot;FastMCP&quot;">​</a></h3><ul><li><strong>版本</strong>: 2.4.0</li><li><strong>作用</strong>: MCP 工具框架</li><li><strong>开源协议</strong>: MIT License</li></ul><h2 id="网络请求" tabindex="-1">网络请求 <a class="header-anchor" href="#网络请求" aria-label="Permalink to &quot;网络请求&quot;">​</a></h2><h3 id="httpx" tabindex="-1">HTTPX <a class="header-anchor" href="#httpx" aria-label="Permalink to &quot;HTTPX&quot;">​</a></h3><ul><li><strong>版本</strong>: 0.28.1</li><li><strong>作用</strong>: 异步 HTTP 客户端</li><li><strong>开源协议</strong>: BSD License</li></ul><h3 id="openai" tabindex="-1">OpenAI <a class="header-anchor" href="#openai" aria-label="Permalink to &quot;OpenAI&quot;">​</a></h3><ul><li><strong>版本</strong>: 1.79.0</li><li><strong>作用</strong>: OpenAI API 客户端</li><li><strong>开源协议</strong>: MIT License</li></ul><h2 id="数据过滤" tabindex="-1">数据过滤 <a class="header-anchor" href="#数据过滤" aria-label="Permalink to &quot;数据过滤&quot;">​</a></h2><h3 id="django-filter" tabindex="-1">Django Filter <a class="header-anchor" href="#django-filter" aria-label="Permalink to &quot;Django Filter&quot;">​</a></h3><ul><li><strong>版本</strong>: 25.1</li><li><strong>作用</strong>: Django 查询过滤</li><li><strong>开源协议</strong>: BSD License</li></ul><h2 id="路由扩展" tabindex="-1">路由扩展 <a class="header-anchor" href="#路由扩展" aria-label="Permalink to &quot;路由扩展&quot;">​</a></h2><h3 id="drf-nested-routers" tabindex="-1">DRF Nested Routers <a class="header-anchor" href="#drf-nested-routers" aria-label="Permalink to &quot;DRF Nested Routers&quot;">​</a></h3><ul><li><strong>版本</strong>: 0.94.2</li><li><strong>作用</strong>: 嵌套路由支持</li><li><strong>开源协议</strong>: BSD License</li></ul><h2 id="ai-和机器学习" tabindex="-1">AI 和机器学习 <a class="header-anchor" href="#ai-和机器学习" aria-label="Permalink to &quot;AI 和机器学习&quot;">​</a></h2><h3 id="langgraph" tabindex="-1">LangGraph <a class="header-anchor" href="#langgraph" aria-label="Permalink to &quot;LangGraph&quot;">​</a></h3><ul><li><strong>版本</strong>: 0.4.5</li><li><strong>作用</strong>: 语言模型应用编排框架</li><li><strong>开源协议</strong>: MIT License</li></ul><p>-### LangChain 相关组件</p><ul><li><strong>langchain-openai</strong> (0.3.17): OpenAI 集成</li></ul><ul><li><strong>langchain-ollama</strong> (0.2.2): Ollama 本地模型支持</li><li><strong>langchain-mistralai</strong> (0.2.4): Mistral AI 支持</li><li><strong>langchain-cohere</strong> (0.3.5): Cohere AI 支持</li><li><strong>langchain-fireworks</strong> (0.2.7): Fireworks AI 支持</li><li><strong>langchain-core</strong> (0.3.60): 核心功能</li><li><strong>langgraph-checkpoint-sqlite</strong> (2.0.10): SQLite 检查点存储</li><li><strong>langchain-mcp-adapters</strong> (0.1.7): MCP 适配器</li><li><strong>开源协议</strong>: MIT License</li></ul><h3 id="ai-服务客户端" tabindex="-1">AI 服务客户端 <a class="header-anchor" href="#ai-服务客户端" aria-label="Permalink to &quot;AI 服务客户端&quot;">​</a></h3><ul><li><strong>cohere</strong> (5.15.0): Cohere API 客户端</li><li><strong>fireworks-ai</strong> (0.15.12): Fireworks AI 客户端</li></ul><ul><li><strong>ollama</strong> (0.4.7): Ollama 客户端</li><li><strong>开源协议</strong>: MIT/Apache 2.0 License</li></ul><h3 id="langchain-社区组件" tabindex="-1">LangChain 社区组件 <a class="header-anchor" href="#langchain-社区组件" aria-label="Permalink to &quot;LangChain 社区组件&quot;">​</a></h3><ul><li><strong>langchain-community</strong> (0.3.24): 社区扩展</li><li><strong>langchain-text-splitters</strong> (0.3.8): 文本分割器</li><li><strong>langchain-qdrant</strong> (0.2.1): Qdrant 向量数据库集成</li><li><strong>qdrant-client</strong> (1.11.3): Qdrant 客户端</li><li><strong>fastembed</strong> (&gt;=0.7.0): BM25 稀疏向量编码（混合检索）</li><li><strong>开源协议</strong>: MIT/Apache 2.0 License</li></ul><h2 id="文档处理" tabindex="-1">文档处理 <a class="header-anchor" href="#文档处理" aria-label="Permalink to &quot;文档处理&quot;">​</a></h2><h3 id="文档格式支持" tabindex="-1">文档格式支持 <a class="header-anchor" href="#文档格式支持" aria-label="Permalink to &quot;文档格式支持&quot;">​</a></h3><ul><li><strong>openpyxl</strong> (3.1.5): Excel 文件处理</li><li><strong>pypdf</strong> (5.6.0): PDF 处理</li><li><strong>python-docx</strong> (1.1.2): Word 文档处理</li><li><strong>python-pptx</strong> (1.0.2): PowerPoint 文档处理</li><li><strong>docx2txt</strong> (0.9): Word 文档文本提取</li><li><strong>unstructured</strong> (0.17.2): 非结构化数据处理</li><li><strong>beautifulsoup4</strong> (4.13.3): HTML/XML 解析</li><li><strong>开源协议</strong>: MIT License (除 beautifulsoup4 为 MIT License)</li></ul><h3 id="huggingface-模型依赖-已弃用" tabindex="-1">HuggingFace 模型依赖（已弃用） <a class="header-anchor" href="#huggingface-模型依赖-已弃用" aria-label="Permalink to &quot;HuggingFace 模型依赖（已弃用）&quot;">​</a></h3><p>⚠️ <strong>注意</strong>: 从当前版本开始，项目已改用 <code>CustomAPIEmbeddings</code> 通过 API 调用嵌入模型，无需安装以下大型依赖包。</p><p>如需使用本地嵌入模型（不推荐），需要安装以下依赖（总计约 1GB+）：</p><ul><li><strong>sentence-transformers</strong> (4.1.0): 句子转换模型 (~90MB模型文件)</li><li><strong>torch</strong> (2.7.1): PyTorch 框架 (~800MB+)</li><li><strong>transformers</strong> (4.52.4): HuggingFace transformers 库</li><li><strong>huggingface-hub</strong> (0.32.4): HuggingFace 模型下载</li><li><strong>开源协议</strong>: Apache License 2.0</li></ul><p><strong>推荐方式</strong>: 使用 API 嵌入服务（OpenAI、Azure、Ollama 等），无需下载大型模型文件。</p><h2 id="自然语言处理" tabindex="-1">自然语言处理 <a class="header-anchor" href="#自然语言处理" aria-label="Permalink to &quot;自然语言处理&quot;">​</a></h2><h3 id="nltk" tabindex="-1">NLTK <a class="header-anchor" href="#nltk" aria-label="Permalink to &quot;NLTK&quot;">​</a></h3><ul><li><strong>版本</strong>: &gt;=3.8.0</li><li><strong>作用</strong>: 自然语言处理工具包，用于文本分割和处理</li><li><strong>开源协议</strong>: Apache License 2.0</li></ul><h3 id="tiktoken" tabindex="-1">Tiktoken <a class="header-anchor" href="#tiktoken" aria-label="Permalink to &quot;Tiktoken&quot;">​</a></h3><ul><li><strong>版本</strong>: &gt;=0.5.0</li><li><strong>作用</strong>: Token 计数库，用于上下文长度计算和 Token 管理</li><li><strong>开源协议</strong>: MIT License</li></ul><h2 id="实时通信" tabindex="-1">实时通信 <a class="header-anchor" href="#实时通信" aria-label="Permalink to &quot;实时通信&quot;">​</a></h2><h3 id="django-channels" tabindex="-1">Django Channels <a class="header-anchor" href="#django-channels" aria-label="Permalink to &quot;Django Channels&quot;">​</a></h3><ul><li><strong>版本</strong>: 4.3.2</li><li><strong>作用</strong>: Django WebSocket 支持</li><li><strong>开源协议</strong>: BSD License</li></ul><h3 id="channels-redis" tabindex="-1">Channels Redis <a class="header-anchor" href="#channels-redis" aria-label="Permalink to &quot;Channels Redis&quot;">​</a></h3><ul><li><strong>版本</strong>: 4.3.0</li><li><strong>作用</strong>: Channels 的 Redis 后端</li><li><strong>开源协议</strong>: BSD License</li></ul><h3 id="daphne" tabindex="-1">Daphne <a class="header-anchor" href="#daphne" aria-label="Permalink to &quot;Daphne&quot;">​</a></h3><ul><li><strong>版本</strong>: 4.2.1</li><li><strong>作用</strong>: ASGI 服务器，支持 HTTP、HTTP2 和 WebSocket</li><li><strong>开源协议</strong>: BSD License</li></ul><h2 id="异步任务" tabindex="-1">异步任务 <a class="header-anchor" href="#异步任务" aria-label="Permalink to &quot;异步任务&quot;">​</a></h2><h3 id="celery" tabindex="-1">Celery <a class="header-anchor" href="#celery" aria-label="Permalink to &quot;Celery&quot;">​</a></h3><ul><li><strong>版本</strong>: 5.4.0</li><li><strong>作用</strong>: 分布式任务队列</li><li><strong>开源协议</strong>: BSD License</li></ul><h3 id="redis" tabindex="-1">Redis <a class="header-anchor" href="#redis" aria-label="Permalink to &quot;Redis&quot;">​</a></h3><ul><li><strong>版本</strong>: 5.2.0</li><li><strong>作用</strong>: Redis 客户端，用作 Celery 的 Broker 和 Backend</li><li><strong>开源协议</strong>: MIT License</li></ul><h2 id="数据库驱动" tabindex="-1">数据库驱动 <a class="header-anchor" href="#数据库驱动" aria-label="Permalink to &quot;数据库驱动&quot;">​</a></h2><h3 id="psycopg2" tabindex="-1">Psycopg2 <a class="header-anchor" href="#psycopg2" aria-label="Permalink to &quot;Psycopg2&quot;">​</a></h3><ul><li><strong>版本</strong>: 2.9.10</li><li><strong>作用</strong>: PostgreSQL 数据库驱动（Django ORM 使用）</li><li><strong>开源协议</strong>: LGPL License</li></ul><h3 id="psycopg3" tabindex="-1">Psycopg3 <a class="header-anchor" href="#psycopg3" aria-label="Permalink to &quot;Psycopg3&quot;">​</a></h3><ul><li><strong>版本</strong>: 3.3.1</li><li><strong>作用</strong>: PostgreSQL 数据库驱动（LangGraph Checkpoint 使用）</li><li><strong>开源协议</strong>: LGPL License</li></ul><h2 id="测试与自动化" tabindex="-1">测试与自动化 <a class="header-anchor" href="#测试与自动化" aria-label="Permalink to &quot;测试与自动化&quot;">​</a></h2><h3 id="pytest" tabindex="-1">Pytest <a class="header-anchor" href="#pytest" aria-label="Permalink to &quot;Pytest&quot;">​</a></h3><ul><li><strong>版本</strong>: &gt;=8.0.0</li><li><strong>作用</strong>: Python 测试框架</li><li><strong>开源协议</strong>: MIT License</li></ul><h3 id="playwright" tabindex="-1">Playwright <a class="header-anchor" href="#playwright" aria-label="Permalink to &quot;Playwright&quot;">​</a></h3><ul><li><strong>版本</strong>: &gt;=1.40.0</li><li><strong>作用</strong>: 浏览器自动化工具，用于端到端测试</li><li><strong>开源协议</strong>: Apache License 2.0</li></ul><h2 id="asgi-服务器" tabindex="-1">ASGI 服务器 <a class="header-anchor" href="#asgi-服务器" aria-label="Permalink to &quot;ASGI 服务器&quot;">​</a></h2><h3 id="uvicorn" tabindex="-1">Uvicorn <a class="header-anchor" href="#uvicorn" aria-label="Permalink to &quot;Uvicorn&quot;">​</a></h3><ul><li><strong>版本</strong>: 0.34.0</li><li><strong>作用</strong>: ASGI 服务器，支持 WebSocket</li><li><strong>开源协议</strong>: BSD License</li></ul><h2 id="语言检测" tabindex="-1">语言检测 <a class="header-anchor" href="#语言检测" aria-label="Permalink to &quot;语言检测&quot;">​</a></h2><h3 id="langdetect" tabindex="-1">Langdetect <a class="header-anchor" href="#langdetect" aria-label="Permalink to &quot;Langdetect&quot;">​</a></h3><ul><li><strong>版本</strong>: 1.0.9</li><li><strong>作用</strong>: 语言检测库</li><li><strong>开源协议</strong>: Apache License 2.0</li></ul><h2 id="markdown-处理" tabindex="-1">Markdown 处理 <a class="header-anchor" href="#markdown-处理" aria-label="Permalink to &quot;Markdown 处理&quot;">​</a></h2><h3 id="markdown" tabindex="-1">Markdown <a class="header-anchor" href="#markdown" aria-label="Permalink to &quot;Markdown&quot;">​</a></h3><ul><li><strong>版本</strong>: 3.9</li><li><strong>作用</strong>: Markdown 文档解析和处理</li><li><strong>开源协议</strong>: BSD License</li></ul>',92)]))}const b=r(o,[["render",t]]);export{u as __pageData,b as default};
